select.if:
select b.*, c.id as contractId, c.number as contractNumber, ct.id as firstPartyId, ct.name as firstPartyName, cf.taxpayer_code as firstPartyTaxpayerCode, cf.addr_phone as firstPartyAddrPhone, cf.bank_account as firstPartyBankAccount, d.id as secondPartyId, d.name as secondPartyName, d.name as secondPartyTaxpayerCode, d.name as secondPartyAddrPhone, d.name as secondPartyBankAccount, up.id as payeeId, up.real_name as payeeName, ua.id as approvalId, ua.real_name as approvalName from t_bill b left join t_contract c on b.contract_id = c.id left join t_customer ct on b.first_party_id = ct.id left join t_financial_info cf on cf.customer_id = ct.id left join tb_dept d on d.id = b.second_party_id left join tb_user up on b.payee_id = up.id left join tb_user ua on b.approval_id = ua.id
and ct.name like concat('%', #{otherPartyName,jdbcType=VARCHAR},'%')
and b.number like concat('%', #{number,jdbcType=VARCHAR},'%')
and b.status = 0 and b.direct = #{direct}
order by b.update_time desc limit #{pageStart}, #{pageSize}

select b.*, c.id as contractId, c.number as contractNumber, ct.id as secondPartyId, ct.name as secondPartyName, cf.taxpayer_code as secondPartyTaxpayerCode, cf.addr_phone as secondPartyAddrPhone, cf.bank_account as secondPartyBankAccount, d.id as firstPartyId, d.name as firstPartyName, d.name as firstPartyTaxpayerCode, d.name as firstPartyAddrPhone, d.name as firstPartyBankAccount, up.id as payeeId, up.real_name as payeeName, ua.id as approvalId, ua.real_name as approvalName from t_bill b left join t_contract c on b.contract_id = c.id left join t_supplier ct on b.second_party_id = ct.id left join t_financial_info cf on cf.supplier_id = ct.id left join tb_dept d on d.id = b.first_party_id left join tb_user up on b.payee_id = up.id left join tb_user ua on b.approval_id = ua.id
and ct.name like concat('%', #{otherPartyName,jdbcType=VARCHAR},'%')
and b.number like concat('%', #{number,jdbcType=VARCHAR},'%')
and b.status = 0 and b.direct = #{direct}
order by b.update_time desc limit #{pageStart}, #{pageSize}

mapper.select:
select b.*, c.id as contractId, c.number as contractNumber, ct.id as firstPartyId, ct.name as firstPartyName, cf.taxpayer_code as firstPartyTaxpayerCode, cf.addr_phone as firstPartyAddrPhone, cf.bank_account as firstPartyBankAccount, d.id as secondPartyId, d.name as secondPartyName, d.name as secondPartyTaxpayerCode, d.name as secondPartyAddrPhone, d.name as secondPartyBankAccount, up.id as payeeId, up.real_name as payeeName, ua.id as approvalId, ua.real_name as approvalName from t_bill b left join t_contract c on b.contract_id = c.id left join t_customer ct on b.first_party_id = ct.id left join t_financial_info cf on cf.customer_id = ct.id left join tb_dept d on d.id = b.second_party_id left join tb_user up on b.payee_id = up.id left join tb_user ua on b.approval_id = ua.id
select b.*, c.id as contractId, c.number as contractNumber, ct.id as secondPartyId, ct.name as secondPartyName, cf.taxpayer_code as secondPartyTaxpayerCode, cf.addr_phone as secondPartyAddrPhone, cf.bank_account as secondPartyBankAccount, d.id as firstPartyId, d.name as firstPartyName, d.name as firstPartyTaxpayerCode, d.name as firstPartyAddrPhone, d.name as firstPartyBankAccount, up.id as payeeId, up.real_name as payeeName, ua.id as approvalId, ua.real_name as approvalName from t_bill b left join t_contract c on b.contract_id = c.id left join t_supplier ct on b.second_party_id = ct.id left join t_financial_info cf on cf.supplier_id = ct.id left join tb_dept d on d.id = b.first_party_id left join tb_user up on b.payee_id = up.id left join tb_user ua on b.approval_id = ua.id
and c.id = #{contractId,jdbcType=INTEGER}
and b.status = 0
order by b.update_time desc limit #{pageStart}, #{pageSize}

mapper.update:
UPDATE t_bill
number=#{number}, contract_id=#{contractId}, first_party_id=#{firstPartyId}, second_party_id=#{secondPartyId}, bill_time=#{billTime}, total_amount=#{totalAmount}, total_tax_amount=#{totalTaxAmount}, chinese_amount=#{chineseAmount}, numeral_amount=#{numeralAmount}, remark=#{remark}, payee_id=#{payeeId}, approval_id=#{approvalId}, bill_to_party=#{billToParty}, direct=#{direct}, create_time=#{createTime}, create_by=#{createBy}, update_time=#{updateTime}, update_by=#{updateBy}, version_=#{version}, status=#{status}, folder_id=#{folderId}, dept_id=#{deptId},
WHERE id = #{id}

